import * as React from "react";
import "./New.css";
import Script from './Script'
// import AboutPage from "./About";
// import "./About";
// import "./services";
import { Outlet, Link } from "react-router-dom";
import { useLocation } from "react-router-dom";
import { NavLink } from "react-router-dom";

function Header() {
  const location = useLocation();

  const isHome = location.pathname === "/";
  return (
    <div>
      <header class="header">
        <section class="flex">
        <NavLink to="/">
          <a class="logo">
            Mercado Escolar
          </a>
          </NavLink>

          <nav class="navbar">
            {/* <a href="home.html">Home</a> */}
            <Link to="/" className="nav-link">
              Home
            </Link>
            <Link to="/about" className="nav-link">
              About
            </Link>
            <Link to="/services" className="nav-link">
              Services
            </Link>
            <Link to="/contact" className="nav-link">
              Contact
            </Link>
            <Link to="/blogs" className="nav-link">
              Blog
            </Link>

            {/* <a href="{blog}">About</a> */}
            {/* <a href="menu.html">Services</a>
            <a href="contact.html">Contact</a>
            <a href="blog.html">Blog</a> */}
          </nav>

          <div class="icons">
          <NavLink to="/SearchPage">
              <i class="fas fa-search"></i>
              </NavLink>
            {!isHome && (
            <NavLink to="/CartPage"> <div id="user-btn" class="fas fa-shopping-cart">
                {" "}
                <span>(3)</span>
              </div>
              </NavLink>

            )}{" "}
            <div id="user-btn" class="fas fa-user"></div>
            <div id="menu-btn" class="fas fa-bars"></div>
          </div>

          <div class="profile">
            <p class="name">user name</p>
            <div class="flex">
              <a href="profile.html" class="btn">
                profile
              </a>
              <a href="." class="delete-btn">
                logout
              </a>
            </div>
            <div class="flex">
              <a href="adminprofile.html" class="btn">
                admin
              </a>
              <a href="businessprofile.html" class="btn">
                Business
              </a>
            </div>
            <div>
              <a href="superadmin.html" class="btn">
                SUPERADMIN
              </a>
            </div>
            <p class="account">
              <a href="login.html">login</a> or
              <a href="register.html">register</a>
            </p>
          </div>
        </section>
      </header>
      <Outlet />
    </div>
  );
}
export default Header;



//script.js

let navbar = document.querySelector('.header .flex .navbar');
let profile = document.querySelector('.header .flex .profile');

document.querySelector('#menu-btn').onclick = () =>{
   navbar.classList.toggle('active');
   profile.classList.remove('active');
}

document.querySelector('#user-btn').onclick = () =>{
   profile.classList.toggle('active');
   navbar.classList.remove('active');
}

window.onscroll = () =>{
   profile.classList.remove('active');
   navbar.classList.remove('active');
}
