import React, { useState,useEffect } from "react";
import "./New.css";
import { Outlet, Link } from "react-router-dom";
import { useLocation } from "react-router-dom";
import { NavLink } from "react-router-dom";
import './Layout.css';
function Header() {
  const location = useLocation();
  const isHome = location.pathname === "/";
  const [windDim,detectWindW]=useState({
    winddowWid:window.innerWidth,
  }
  )
  const detectSize=()=>{
    detectWindW({
      winddowWid:window.innerWidth,
    })
  }
  useEffect(()=>{
window.addEventListener('resize',detectSize)
return()=>{
  window.removeEventListener('resize',detectSize)
}  
},[windDim])
  return (
    <div>

      <header className="header">
        <section class="flex">
          <NavLink to="/">
            <a class="logo">
              Mercado Escolar
            </a>
            <h1>{windDim.winddowWid}</h1>

          </NavLink>
          {!windDim.winddowWid>=600 &&(
          <nav className="navbar">
            <Link to="/" className="nav-link">
              Home
            </Link>
            <Link to="/about" className="nav-link">
              About
            </Link>
            <Link to="/services" className="nav-link">
              Services
            </Link>
            <Link to="/contact" className="nav-link">
              Contact
            </Link>
            <Link to="/blogs" className="nav-link">
              Blog
            </Link>
          </nav>
          )}

          <div class="icons">
            <div className="lefticons">
              <NavLink to="/SearchPage">
                <i class="fas fa-search"></i>
              </NavLink>
              {!isHome && (
                <NavLink to="/CartPage">
                  <div id="user-btn" class="fas fa-shopping-cart">
                    <span>(3)</span>
                  </div>
                </NavLink>

              )}
            </div>
            <div class="dropdown">
              <button class="dropbtn">
                <i class="fa fa-user"></i>
              </button>
              <div class="dropdown-content">
                <li><NavLink to="/ProfilePage">Profile</NavLink></li>
                <li><NavLink to="/">Logout</NavLink></li>
                <li><NavLink to="/AdminProfile">Admin</NavLink></li>
                <li><NavLink to="/BusinessProfile">Business</NavLink></li>
                <li><NavLink to="/SuperAdmin">Super Admin</NavLink></li>
              </div>
            </div>
            <div class="dropdown">
              <button class="dropbtn">
                <i class="fa fa-sign-in"></i>
              </button>
              <div class="dropdown-content">
                <li><NavLink to="/Login">Login</NavLink></li>
                <li><NavLink to="/Register">Register</NavLink></li>
              </div>
            </div>
          </div>
        </section>
      </header>
      <Outlet />
    </div>
  );
}
export default Header;
